$(document).ready(function () {
    $(document).on("click", ".header__navbar--top>ul>li>a", function (o) {
        o.preventDefault();
        var t = $(this).attr("href"), e = $(t), l = e.offset().top;
        return $("html,body").animate({scrollTop: l}, 1e3), !1
    }), $(document).on("click", "#Go_Top", function () {
        return $("html, body").animate({scrollTop: 0}, "slow"), !1
    })
}),
    $(window).scroll(function () {
        var o = $("#Go_Top");
        $(this).hide().removeAttr("href"), $(window).scrollTop() >= "250" && $(this).fadeIn("slow"), $(window).scrollTop() <= "250" ? $(o).fadeOut("slow") : $(o).fadeIn("slow")
    }), $(window).load(function () {
    $(".bilder__slider").owlCarousel({
        loop: !0,
        margin: 10,
        nav: !1,
        dots: !1,
        autoplay: !1,
        autoplayTimeout: 2e3,
        responsive: {0: {items: 1}, 600: {items: 2}, 1e3: {items: 3}, 1600: {items: 5}}
    })
});